import React, { useState, useEffect } from "react";
import axios from "axios";
import { NavLink } from "react-router-dom";
import style from "../StudentDashboard.module.css";

// Assuming the baseURL is set globally, or set it locally if needed
axios.defaults.baseURL = 'http://localhost:5000/api';

function Subject() {
    const [exams, setExams] = useState([]);
    const [isLoading, setIsLoading] = useState(true);  // Add loading state
    const [error, setError] = useState(null);  // Add error state

    useEffect(() => {
        // Fetch exams when the component mounts
        const fetchExams = async () => {
            setIsLoading(true);
            try {
                const response = await axios.get("/exams");
                setExams(response.data);
                setError(null);  // Clear previous errors on successful fetch
            } catch (error) {
                console.error('Error fetching exams:', error);
                setError('Failed to fetch exams. Please try again later.');  // Set an error message
            }
            setIsLoading(false);  // Set loading to false after fetch completes
        };

        fetchExams();
    }, []);

    return (
        <>
            <div id={style.displayBoxHeadingBox}>
                <h1>Choose Subjects</h1>
                {error && <p style={{ color: 'red' }}>{error}</p>}  // Display error message if there is an error
            </div>
            
            {isLoading ? (
                <p>Loading exams...</p>  // Display loading message while fetching data
            ) : (
                exams.map((exam, i) => (
                    <div id={style.displayBoxSubjectBox} key={i}>
                        <div id={style.subjectText}>
                            <span>{exam.name}</span>
                        </div>
                        <div id={style.subjectButton}>
                            <NavLink exact to={`/StudentDashboard/Exam/${exam.name}`}>
                                <button>Go to Exam</button>
                            </NavLink>
                        </div>
                    </div>
                ))
            )}
        </>
    );
}

export default Subject;
