import React, { useState, useEffect } from "react";
import axios from "axios";
import { NavLink } from "react-router-dom";
import style from "../StudentDashboard.module.css";

axios.defaults.baseURL = 'http://localhost:5000/api';

function Subject() {
    const [exams, setExams] = useState([]);
    const [error, setError] = useState(""); // State to hold any error message

    useEffect(() => {
        const fetchExams = async () => {
            try {
                const response = await axios.get("/exams");
                setExams(response.data);
                if (response.data.length === 0) {
                    setError("No exams available at the moment.");
                }
            } catch (error) {
                console.error('Error fetching exams:', error);
                setError("Failed to fetch exams. Please try again later.");
            }
        };

        fetchExams();
    }, []);

    return (
        <>
            <div className={style.displayBoxHeadingBox}>
                <h1>Choose Subjects</h1>
                {error && <p className={style.error}>{error}</p>}
            </div>

            {exams.map((exam, i) => (
                <div className={style.displayBoxSubjectBox} key={i}>
                    <div className={style.subjectText}>
                        <h2>{exam.name}</h2> {/* Bolder subject name */}
                    </div>
                    <div className={style.subjectDetail}>
                        <p><strong>Description:</strong> {exam.desc}</p> {/* Added description */}
                        <p><strong>Pass Marks:</strong> {exam.passMarks}</p>
                        <p><strong>Total Marks:</strong> {exam.marks}</p>
                    </div>
                    <div className={style.subjectButton}>
                        <NavLink exact to={`/StudentDashboard/Exam/${exam._id}`}>
                            <button>Go to Exam</button>
                        </NavLink>
                    </div>
                </div>
            ))}
        </>
    );
}

export default Subject;
