import React, { useState, useEffect } from "react";
import axios from "axios";
import { NavLink } from "react-router-dom";
import style from "./SubjectComponent/Subject.module.css";

// In a config file or at the top of your component file
axios.defaults.baseURL = 'http://localhost:5000/api';

function Exam() {
    const [exams, setExams] = useState([]);
    const [newExam, setNewExam] = useState({
        name: "",
        desc: "",
        level: "",
        passMarks: "",
        totalQuestion: "",
        marks: "",
        questions: []  // Initial empty questions array
    });

    useEffect(() => {
        axios.get('/api/exams')
            .then(response => setExams(response.data))
            .catch(error => console.error('Error fetching exams:', error));
    }, []);

    function handleInputChange(e) {
        setNewExam({ ...newExam, [e.target.name]: e.target.value });
    }

    function handleAddExam() {
        axios.post('/api/exams', newExam)
            .then(() => {
                alert('Exam added successfully');
                setNewExam({
                    name: "",
                    desc: "",
                    level: "",
                    passMarks: "",
                    totalQuestion: "",
                    marks: "",
                    questions: []
                });
            })
            .catch(error => console.error('Error adding new exam:', error));
    }

    return (
        <div>
            <h1>Add New Exam</h1>
            <input name="name" value={newExam.name} onChange={handleInputChange} placeholder="Exam Name" />
            <input name="desc" value={newExam.desc} onChange={handleInputChange} placeholder="Description" />
            <input name="level" value={newExam.level} onChange={handleInputChange} placeholder="Level" />
            <input name="passMarks" value={newExam.passMarks} onChange={handleInputChange} placeholder="Pass Marks" />
            <input name="totalQuestion" value={newExam.totalQuestion} onChange={handleInputChange} placeholder="Total Questions" />
            <input name="marks" value={newExam.marks} onChange={handleInputChange} placeholder="Marks" />
            <button onClick={handleAddExam}>Add Exam</button>
        </div>
    );
}

export default Exam;
