import React, { useState, useEffect } from "react";
import axios from "axios";
import { useParams, NavLink, useHistory } from "react-router-dom";
import style from "../StudentDashboard.module.css";

// Set base URL for Axios requests
axios.defaults.baseURL = 'http://localhost:5000/api';

function Exam() {
    const { category } = useParams();
    const history = useHistory();
    const [exams, setExams] = useState([]);

    useEffect(() => {
        fetchExams();
    }, []);

    const fetchExams = async () => {
        try {
            const response = await axios.get("/exams");
            setExams(response.data);
        } catch (error) {
            console.error('Error fetching exams:', error);
        }
    };

    const handleGoToExam = async (examId) => {
        try {
            // Start proctoring
            const proctoringResponse = await axios.post("http://localhost:5000/start_proctoring");
            if (!proctoringResponse.ok) {
                throw new Error("Failed to start proctoring");
            }

            // If proctoring started successfully, navigate to the exam page
            history.push(`/StudentDashboard/Exam/${category}/${examId}`);
        } catch (error) {
            console.error("Error:", error);
            // Handle error
        }
    };

    return (
        <>
            <div id={style.displayBoxHeadingBox}>
                <h1>All {category} Exams</h1>
            </div>
            {
                exams.map((data, i) => {
                    if (data.name === category)
                        return (
                            <div id={style.displayBoxExamBox} key={i}>
                                <div id={style.div1}><span>{data.name}</span></div>
                                <div id={style.div2}><span>Exam ID: {data._id}</span></div>
                                <div id={style.div2}><span>Exam Description: {data.desc}</span></div>
                                <div id={style.div3}><span>Pass Marks: {data.passMarks}</span></div>
                                <div id={style.div4}><span>Total Marks: {data.marks}</span></div>
                                <div id={style.div5}>
                                    <NavLink exact to={`/StudentDashboard/Exam/${data.name}/${data._id}`}>
                                        <button onClick={() => handleGoToExam(data._id)}>Go to Exam</button>
                                    </NavLink>
                                </div>
                            </div>
                        );
                    return <React.Fragment key={i}></React.Fragment>;
                })
            }
        </>
    );
}

export default Exam;
