import React, { useState, useEffect } from "react";
import axios from "axios";
import { NavLink } from "react-router-dom";
import style from "../SubjectComponent/Subject.module.css";

// Set base URL for Axios requests
axios.defaults.baseURL = 'http://localhost:5000/api';

function Exam() {
    const [display, setDisplay] = useState(false);
    const [exams, setExams] = useState([]);
    const [newExam, setNewExam] = useState({
        name: "",
        desc: "",
        level: "",
        passMarks: "",
        totalQuestion: "",
        marks: ""
    });

    useEffect(() => {
        fetchExams();
    }, []);

    const fetchExams = async () => {
        try {
            const response = await axios.get("/exams");
            setExams(response.data);
        } catch (error) {
            console.error('Error fetching exams:', error);
        }
    };

    const handleAddExam = () => {
        setDisplay(true);
    };

    const handleCloseExam = () => {
        setDisplay(false);
    };

    const handleInputChange = (e) => {
        setNewExam({
            ...newExam,
            [e.target.name]: e.target.value
        });
    };

    const handleAddNewExam = async () => {
        try {
            await axios.post("/exams", newExam);
            fetchExams(); // Refresh exam list after adding a new exam
            setNewExam({
                name: "",
                desc: "",
                level: "",
                passMarks: "",
                totalQuestion: "",
                marks: ""
            });
            setDisplay(false);
        } catch (error) {
            console.error('Error adding new exam:', error);
        }
    };

    const deleteExam = async (id) => {
        try {
            await axios.delete(`/exams/${id}`);
            fetchExams(); // Refresh exam list after deleting an exam
        } catch (error) {
            console.error('Error deleting exam:', error);
        }
    };

    const addQuestionToExam = async (examId, question) => {
        try {
            await axios.post(`/exams/${examId}/questions`, question);
            fetchExams(); // Refresh exam list after adding a new question
        } catch (error) {
            console.error('Error adding question to exam:', error);
        }
    };

    const handleAddQuestion = (examId) => {
        const question = {
            questionName: newExam.questionName,
            questionText: newExam.questionText,
            options: [newExam.optionA, newExam.optionB, newExam.optionC, newExam.optionD],
            correctOption: newExam.correctOption,
            subject: newExam.subject
        };
        addQuestionToExam(examId, question);
    };

    return (
        <>
            <div id={style.displayHeadingBox}>
                <h2>Exam List</h2>
            </div>

            <div id={style.tableBox}>
                <table>
                    <thead>
                        <tr>
                            <th id={style.center}>Exam Name</th>
                            <th id={style.center}>Exam Desc.</th>
                            <th id={style.center}>Exam Creation Date</th>
                            <th id={style.center}>Exam Level</th>
                            <th id={style.center}>Options</th>
                        </tr>
                    </thead>
                    <tbody id={style.tbody}>
                        {exams.map((data, i) => (
                            <tr key={i}>
                                <td>{data.name}</td>
                                <td>{data.desc}</td>
                                <td>{data.date}</td>
                                <td>{data.level}</td>
                                <td>
                                    <NavLink exact to={`/AdminDashboard/Exam/Details/${data.id}`}>
                                        <button>Details</button>
                                    </NavLink>

                                    <NavLink exact to={`/AdminDashboard/Exam/ViewQuestion/${data.id}`}>
                                        <button>View Question</button>
                                    </NavLink>

                                    <NavLink exact to={`/AdminDashboard/Exam/AddQuestion/${data.id}`}>
                                        <button onClick={() => handleAddQuestion(data.id)}>Add Question</button>
                                    </NavLink>

                                    <button onClick={() => deleteExam(data.id)}>Delete</button>
                                </td>
                            </tr>
                        ))}
                    </tbody>
                </table>
            </div>

            <div id={style.addSubjectBox}>
                <button onClick={handleAddExam}>Add Exam</button>
            </div>

            {display && (
                <div id={style.addBox}>
                    <label htmlFor="questionName">Question Name</label>
                    <input id="questionName" name="questionName" type="text" value={newExam.questionName} onChange={handleInputChange} placeholder="Enter Question Name" />

                    <label htmlFor="questionText">Enter Question</label>
                    <input id="questionText" name="questionText" type="text" value={newExam.questionText} onChange={handleInputChange} placeholder="Enter Question" />

                    <label htmlFor="optionA">Enter Option A</label>
                    <input id="optionA" name="optionA" type="text" value={newExam.optionA} onChange={handleInputChange} placeholder="Enter Option A" />

                    <label htmlFor="optionB">Enter Option B</label>
                    <input id="optionB" name="optionB" type="text" value={newExam.optionB} onChange={handleInputChange} placeholder="Enter Option B" />

                    <label htmlFor="optionC">Enter Option C</label>
                    <input id="optionC" name="optionC" type="text" value={newExam.optionC} onChange={handleInputChange} placeholder="Enter Option C" />

                    <label htmlFor="optionD">Enter Option D</label>
                    <input id="optionD" name="optionD" type="text" value={newExam.optionD} onChange={handleInputChange} placeholder="Enter Option D" />

                    <label htmlFor="correctOption">Enter Question Answer</label>
                    <input id="correctOption" name="correctOption" type="text" value={newExam.correctOption} onChange={handleInputChange} placeholder="Enter Question answer (don't write option A,B,C,D)" />

                    <label htmlFor="subject">Enter Subject</label>
                    <input id="subject" name="subject" type="text" value={newExam.subject} onChange={handleInputChange} placeholder="Enter Subject" />

                    <div id={style.buttonBox}>
                        <button onClick={handleAddNewExam}>Add</button>
                        <button onClick={handleCloseExam}>Go back</button>
                    </div>
                </div>
            )}
        </>
    );
}

export default Exam;
