import React, { useState } from "react";
import { useParams, NavLink } from "react-router-dom";
import style from "../StudentDashboard.module.css";

function Exam() {
    let { category } = useParams();

    const allExam = [
        {
            id: 1,
            name: "Maths",
            desc: "Mathematics exam description",
            passMarks: 50,
            marks: 100
        },
        {
            id: 2,
            name: "Science",
            desc: "Science exam description",
            passMarks: 60,
            marks: 120
        },
        // Add more static data as needed
    ];

    // Function to start the exam
    const startExam = async (examId) => {
        try {
            const response = await fetch(`/start_exam/${examId}`, {
                method: "POST",
                headers: {
                    "Content-Type": "application/json"
                },
                // You can send additional data in the request body if needed
                body: JSON.stringify({ /* Additional data */ })
            });
            if (!response.ok) {
                throw new Error("Failed to start exam");
            }
            // Handle successful response, e.g., show a success message
        } catch (error) {
            console.error("Error:", error);
            // Handle error, e.g., show an error message to the user
        }
    };

    // Function to end the exam
    const endExam = async () => {
        try {
            const response = await fetch("/end_exam", {
                method: "POST",
                headers: {
                    "Content-Type": "application/json"
                },
                // You can send additional data in the request body if needed
                body: JSON.stringify({ /* Additional data */ })
            });
            if (!response.ok) {
                throw new Error("Failed to end exam");
            }
            // Handle successful response, e.g., show a success message
        } catch (error) {
            console.error("Error:", error);
            // Handle error, e.g., show an error message to the user
        }
    };

    return (
        <>
            <div id={style.displayBoxHeadingBox}>
                <h1>All {category} Exam</h1>
            </div>
            {
                allExam.map((data, i) => {
                    if (data.name === category)
                        return (
                            <div id={style.displayBoxExamBox} key={i}>
                                <div id={style.div1}><span>{data.name}</span></div>
                                <div id={style.div2}><span>Exam ID: {data.id}</span></div>
                                <div id={style.div2}><span>Exam Description: {data.desc}</span></div>
                                <div id={style.div3}><span>Pass Marks: {data.passMarks}</span></div>
                                <div id={style.div4}><span>Total Marks: {data.marks}</span></div>
                                <div id={style.div5}>
                                    <button onClick={() => startExam(data.id)}>Go to Exam</button>
                                    <button onClick={endExam}>Submit Exam</button>
                                </div>
                            </div>
                        );
                    return <React.Fragment key={i}></React.Fragment>;
                })
            }
        </>
    );
}

export default Exam;
